import React, { useState, useRef, useEffect } from "react";
import { Search, MessageCircle, Send, MoreVertical, Phone, Video, Info, Smile, Paperclip, Mic } from "lucide-react";
import {ConversationItem} from "../compon";

import {ChatWindow} from "../components/InboxPage/ConversationItem"

const InboxPage = () => {
  const [conversations] = useState([
    {
      id: 1,
      name: "Alice Johnson",
      lastMessage: "Can we schedule the call tomorrow?",
      time: "10:30 AM",
      unread: 2,
      profilePhoto: "https://i.pravatar.cc/150?img=5",
    },
    {
      id: 2,
      name: "David Smith",
      lastMessage: "Thanks for the help! 🙏",
      time: "Yesterday",
      unread: 0,
      profilePhoto: "https://i.pravatar.cc/150?img=11",
    },
    {
      id: 3,
      name: "Priya Sharma",
      lastMessage: "I shared the document with you.",
      time: "2 days ago",
      unread: 5,
      profilePhoto: "https://i.pravatar.cc/150?img=32",
    },
    {
      id: 4,
      name: "John Wilson",
      lastMessage: "See you in the meeting!",
      time: "3 days ago",
      unread: 0,
      profilePhoto: "https://i.pravatar.cc/150?img=8",
    },
    {
      id: 5,
      name: "Sarah Davis",
      lastMessage: "Perfect! That works for me.",
      time: "1 week ago",
      unread: 1,
      profilePhoto: "https://i.pravatar.cc/150?img=45",
    },
  ]);

  const [selectedChat, setSelectedChat] = useState(null);
  const [sidebarWidth, setSidebarWidth] = useState(350);
  const [isResizing, setIsResizing] = useState(false);
  const containerRef = useRef(null);

  const handleMouseDown = (e) => {
    setIsResizing(true);
    e.preventDefault();
  };

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!isResizing) return;
      
      const containerRect = containerRef.current.getBoundingClientRect();
      const newWidth = e.clientX - containerRect.left;
      
      if (newWidth >= 280 && newWidth <= 500) {
        setSidebarWidth(newWidth);
      }
    };

    const handleMouseUp = () => {
      setIsResizing(false);
    };

    if (isResizing) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isResizing]);

  const styles = {
    container: {
      display: "flex",
      height: "100vh",
      backgroundColor: "#f9fafb",
      color: "#111827",
      fontFamily: "system-ui, -apple-system, sans-serif",
      position: "relative"
    },
    sidebar: {
      width: `${sidebarWidth}px`,
      backgroundColor: "#fff",
      borderRight: "1px solid #e5e7eb",
      display: "flex",
      flexDirection: "column",
      boxShadow: "2px 0 10px rgba(0,0,0,0.05)",
      position: "relative"
    },
    resizer: {
      position: "absolute",
      top: 0,
      right: 0,
      width: "4px",
      height: "100%",
      backgroundColor: isResizing ? "#2563eb" : "transparent",
      cursor: "col-resize",
      zIndex: 10,
      transition: "background-color 0.2s"
    },
    header: {
      padding: "20px 16px",
      borderBottom: "1px solid #f3f4f6",
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between"
    },
    headerTitle: {
      fontWeight: "700",
      fontSize: "20px",
      color: "#111827"
    },
    searchContainer: {
      padding: "16px",
      position: "relative"
    },
    searchBox: {
      width: "100%",
      padding: "12px 16px 12px 40px",
      borderRadius: "12px",
      border: "1px solid #e5e7eb",
      backgroundColor: "#f9fafb",
      color: "#111827",
      outline: "none",
      fontSize: "14px",
      transition: "all 0.2s"
    },
    searchIcon: {
      position: "absolute",
      left: "28px",
      top: "50%",
      transform: "translateY(-50%)",
      color: "#9ca3af"
    },
    conversations: {
      flex: 1,
      overflowY: "auto"
    },
    chatArea: {
      flex: 1,
      display: "flex",
      backgroundColor: "#fff",
      position: "relative",
      
      
    },
    emptyState: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      height: "100%",
      textAlign: "center",
      padding: "40px",
      
    },
    emptyStateContent: {
      maxWidth: "300px",
    
    },
    emptyIcon: {
      fontSize: "64px",
      marginBottom: "20px",
      opacity: 0.5

    },
    emptyTitle: {
      fontSize: "24px",
      fontWeight: "600",
      color: "#111827",
      marginBottom: "8px"
    },
    emptyDescription: {
      color: "#6b7280",
      marginBottom: "24px",
      lineHeight: "1.5",
    },
    emptyButton: {
      padding: "12px 24px",
      border: "none",
      borderRadius: "12px",
      backgroundColor: "#2563eb",
      color: "#fff",
      cursor: "pointer",
      fontWeight: "600",
      fontSize: "14px",
      transition: "all 0.2s",
      display: "flex",
      alignItems: "center",
      gap: "8px",
      margin: "0 auto"
    }
  };

  return (
    <div style={styles.container} ref={containerRef}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        {/* Resizer */}
        <div
          style={styles.resizer}
          onMouseDown={handleMouseDown}
          onMouseEnter={(e) => {
            if (!isResizing) e.target.style.backgroundColor = "#e5e7eb";
          }}
          onMouseLeave={(e) => {
            if (!isResizing) e.target.style.backgroundColor = "transparent";
          }}
        />

        {/* Header */}
        <div style={styles.header}>
          <div style={styles.headerTitle}>Messages</div>
          <button
            style={{
              padding: "8px",
              borderRadius: "8px",
              border: "none",
              backgroundColor: "transparent",
              cursor: "pointer",
              color: "#6b7280"
            }}
          >
            <MoreVertical size={18} />
          </button>
        </div>

        {/* Search */}
        <div style={styles.searchContainer}>
          <Search style={styles.searchIcon} size={18} />
          <input
            style={styles.searchBox}
            type="text"
            placeholder="Search conversations..."
            onFocus={(e) => {
              e.target.style.borderColor = "#2563eb";
              e.target.style.backgroundColor = "#fff";
            }}
            onBlur={(e) => {
              e.target.style.borderColor = "#e5e7eb";
              e.target.style.backgroundColor = "#f9fafb";
            }}
          />
        </div>

        {/* Conversations */}
        <div style={styles.conversations}>
          {conversations.map((conv) => (
            <ConversationItem
              key={conv.id}
              conversation={conv}
              onClick={() => setSelectedChat(conv)}
              isActive={selectedChat?.id === conv.id}
            />
          ))}
        </div>
      </div>

      {/* Chat Area */}
      <div style={styles.chatArea}>
        {selectedChat ? (
          <ChatWindow conversation={selectedChat} />
        ) : (
          <div style={styles.emptyState}>
            <div style={styles.emptyStateContent}>
              <div style={styles.emptyIcon}>💬</div>
              <h2 style={styles.emptyTitle}>Your Messages</h2>
              <p style={styles.emptyDescription}>
                Select a conversation to start chatting or send a new message to connect with someone.
              </p>
              <button
                style={styles.emptyButton}
                onMouseEnter={(e) => e.target.style.backgroundColor = "#1d4ed8"}
                onMouseLeave={(e) => e.target.style.backgroundColor = "#2563eb"}
              >
                <MessageCircle size={18} />
                New Message
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default InboxPage;
