import React, { useState } from "react";
import { Plus, X } from "lucide-react";

const MentorForm = ({ onSubmit }) => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    // Step 1: Personal Info
    name: "",
    age: "",
    gender: "",
    contact: "",
    address: "",
    // Step 2: Education
    education: "",
    college: "",
    stream: "",
    cgpa: "",
    // Step 3: Experience
    yearsOfExperience: "",
    company: "",
    designation: "",
    linkedin: "",
    // Step 4: Skills
    areasOfProficiency: [],
    certifications: ""
  });

  const [newSkill, setNewSkill] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const addSkill = () => {
    if (
      newSkill.trim() &&
      !formData.areasOfProficiency.includes(newSkill.trim())
    ) {
      setFormData((prev) => ({
        ...prev,
        areasOfProficiency: [...prev.areasOfProficiency, newSkill.trim()],
      }));
      setNewSkill("");
    }
  };

  const removeSkill = (skillToRemove) => {
    setFormData((prev) => ({
      ...prev,
      areasOfProficiency: prev.areasOfProficiency.filter(
        (skill) => skill !== skillToRemove
      ),
    }));
  };

  const nextStep = () => setStep((prev) => Math.min(prev + 1, 4));
  const prevStep = () => setStep((prev) => Math.max(prev - 1, 1));

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  // Progress bar calculation
  const progress = (step / 4) * 100;

  return (
    <div className="card max-w-2xl mx-auto p-6 shadow-lg rounded-lg">
      {/* Progress Bar */}
      <div className="w-full bg-gray-200 rounded-full h-2 mb-6">
        <div
          className="bg-blue-600 h-2 rounded-full transition-all"
          style={{ width: `${progress}%` }}
        ></div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Step 1: Personal Info */}
        {step === 1 && (
          <div>
            <h2 className="text-xl font-bold mb-4">Personal Information</h2>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="Full Name"
              className="input-field mb-3 w-full"
              required
            />
            <input
              type="number"
              name="age"
              value={formData.age}
              onChange={handleInputChange}
              placeholder="Age"
              className="input-field mb-3 w-full"
              required
            />
            <select
              name="gender"
              value={formData.gender}
              onChange={handleInputChange}
              className="input-field mb-3 w-full"
              required
            >
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
            <input
              type="text"
              name="contact"
              value={formData.contact}
              onChange={handleInputChange}
              placeholder="Contact Number"
              className="input-field mb-3 w-full"
              required
            />
            <textarea
              name="address"
              value={formData.address}
              onChange={handleInputChange}
              placeholder="Address"
              className="input-field w-full"
              rows="2"
            />
          </div>
        )}

        {/* Step 2: Education */}
        {step === 2 && (
          <div>
            <h2 className="text-xl font-bold mb-4">Education Details</h2>
            <select
              name="education"
              value={formData.education}
              onChange={handleInputChange}
              className="input-field mb-3 w-full"
              required
            >
              <option value="">Select Highest Education</option>
              <option value="graduation">Graduation</option>
              <option value="post-graduation">Post-Graduation</option>
              <option value="phd">PhD</option>
              <option value="other">Other</option>
            </select>
            <input
              type="text"
              name="college"
              value={formData.college}
              onChange={handleInputChange}
              placeholder="College/University"
              className="input-field mb-3 w-full"
              required
            />
            <input
              type="text"
              name="stream"
              value={formData.stream}
              onChange={handleInputChange}
              placeholder="Field of Study (e.g. Computer Science)"
              className="input-field mb-3 w-full"
              required
            />
            <input
              type="text"
              name="cgpa"
              value={formData.cgpa}
              onChange={handleInputChange}
              placeholder="CGPA / Percentage"
              className="input-field w-full"
            />
          </div>
        )}

        {/* Step 3: Experience */}
        {step === 3 && (
          <div>
            <h2 className="text-xl font-bold mb-4">Professional Experience</h2>
            <input
              type="number"
              name="yearsOfExperience"
              value={formData.yearsOfExperience}
              onChange={handleInputChange}
              placeholder="Years of Experience"
              className="input-field mb-3 w-full"
              min="0"
              max="50"
              required
            />
            <input
              type="text"
              name="company"
              value={formData.company}
              onChange={handleInputChange}
              placeholder="Current/Last Company"
              className="input-field mb-3 w-full"
            />
            <input
              type="text"
              name="designation"
              value={formData.designation}
              onChange={handleInputChange}
              placeholder="Designation/Role"
              className="input-field mb-3 w-full"
            />
            <input
              type="url"
              name="linkedin"
              value={formData.linkedin}
              onChange={handleInputChange}
              placeholder="LinkedIn Profile URL"
              className="input-field w-full"
            />
          </div>
        )}

        {/* Step 4: Skills */}
        {step === 4 && (
          <div>
            <h2 className="text-xl font-bold mb-4">Skills & Proficiency</h2>
            <div className="flex gap-2 mb-3">
              <input
                type="text"
                value={newSkill}
                onChange={(e) => setNewSkill(e.target.value)}
                className="input-field flex-1"
                placeholder="Add a Skill (e.g., Python, AI)"
                onKeyPress={(e) =>
                  e.key === "Enter" && (e.preventDefault(), addSkill())
                }
              />
              <button
                type="button"
                onClick={addSkill}
                className="btn-primary px-4 py-2"
              >
                <Plus className="w-4 h-4" />
              </button>
            </div>
            <div className="flex flex-wrap gap-2 mb-4">
              {formData.areasOfProficiency.map((skill, index) => (
                <span
                  key={index}
                  className="inline-flex items-center gap-2 bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm"
                >
                  {skill}
                  <button
                    type="button"
                    onClick={() => removeSkill(skill)}
                    className="text-blue-600 hover:text-blue-800"
                  >
                    <X className="w-3 h-3" />
                  </button>
                </span>
              ))}
            </div>
            <textarea
              name="certifications"
              value={formData.certifications}
              onChange={handleInputChange}
              placeholder="Certifications (if any)"
              className="input-field w-full"
              rows="2"
            />
          </div>
        )}

        {/* Navigation Buttons */}
        <div className="flex justify-between">
          {step > 1 && (
            <button
              type="button"
              onClick={prevStep}
              className="px-4 py-2 bg-gray-300 rounded-lg"
            >
              Back
            </button>
          )}
          {step < 4 ? (
            <button
              type="button"
              onClick={nextStep}
              className="ml-auto px-4 py-2 bg-blue-600 text-white rounded-lg"
            >
              Next
            </button>
          ) : (
            <button
              type="submit"
              className="ml-auto px-4 py-2 bg-green-600 text-white rounded-lg"
            >
              Submit
            </button>
          )}
        </div>
      </form>
    </div>
  );
};

export default MentorForm;
