import React, { useState } from "react";

const ProfilePage = () => {
  const [userData, setUserData] = useState({
    name: "John Doe",
    type: "mentor", // or 'mentee'
    email: "john@example.com",
    phone: "+91 9876543210",
    college: "MIT",
    stream: "Computer Science",
    education: "Graduation",
    yearsOfExperience: 2,        // Industry experience
    totalExperience: 4,          // Total (Industry + Other)
    skills: ["JavaScript", "React", "Node.js"],
    coins: 120,
    bio: "Passionate learner, focused on improving frontend development skills.",
    profilePhoto: "https://i.pravatar.cc/150?img=32",
  });

  // ✅ Inline CSS styles
  const styles = {
    container: {
      minHeight: "100vh",
      display: "flex",
      justifyContent: "center",
      alignItems: "flex-start",
      backgroundColor: "#f9fafb",
      padding: "40px",
    },
    card: {
      backgroundColor: "#fff",
      borderRadius: "16px",
      boxShadow: "0 4px 10px rgba(0,0,0,0.05)",
      padding: "32px",
      width: "100%",
      maxWidth: "800px",
    },
    header: {
      display: "flex",
      alignItems: "center",
      gap: "20px",
      borderBottom: "1px solid #e5e7eb",
      paddingBottom: "20px",
    },
    profileImg: {
      width: "100px",
      height: "100px",
      borderRadius: "50%",
      objectFit: "cover",
      border: "2px solid #e5e7eb",
    },
    name: {
      fontSize: "24px",
      fontWeight: "bold",
      color: "#111827",
    },
    role: {
      fontSize: "14px",
      fontWeight: "500",
      color: "#2563eb",
      backgroundColor: "#eff6ff",
      padding: "4px 10px",
      borderRadius: "12px",
      display: "inline-block",
      marginTop: "4px",
    },
    section: {
      marginTop: "24px",
    },
    sectionTitle: {
      fontSize: "18px",
      fontWeight: "600",
      color: "#111827",
      marginBottom: "12px",
    },
    subText: {
      fontSize: "14px",
      color: "#6b7280",
      marginBottom: "4px",
    },
    skillsWrapper: {
      display: "flex",
      flexWrap: "wrap",
      gap: "10px",
    },
    skillBadge: {
      backgroundColor: "#f3f4f6",
      padding: "6px 12px",
      borderRadius: "20px",
      fontSize: "14px",
      fontWeight: "500",
      color: "#374151",
    },
    coinBadge: {
      backgroundColor: "#fef3c7",
      color: "#92400e",
      fontSize: "14px",
      fontWeight: "600",
      padding: "6px 12px",
      borderRadius: "12px",
      display: "inline-block",
      marginTop: "8px",
    },
    bio: {
      fontSize: "15px",
      lineHeight: "1.6",
      color: "#374151",
    },
    grid: {
      display: "grid",
      gridTemplateColumns: "1fr 1fr",
      gap: "16px",
      marginTop: "20px",
    },
  };

  return (
   <>
   
   </>
  );
};

export default ProfilePage;
