import React, { useState } from "react";
import {
  Star,
  MapPin,
  Calendar,
  Clock,
  Award,
  Briefcase,
  GraduationCap,
  MessageCircle,
  Globe,
  BookOpen,
  Target,
  CheckCircle,
  Heart,
  Share2,
  Flag,
} from "lucide-react";

const styles = {
  page: {
    minHeight: "100vh",
    backgroundColor: "#f9fafb",
    padding: "2rem 1rem",
  },
  container: {
    maxWidth: "1200px",
    margin: "0 auto",
    display: "grid",
    gap: "2rem",
    gridTemplateColumns: "1fr",
  },
  card: {
    backgroundColor: "#fff",
    borderRadius: "0.5rem",
    border: "1px solid #e5e7eb",
    boxShadow: "0 1px 2px rgba(0,0,0,0.05)",
    padding: "1.5rem",
  },
  profileCard: {
    position: "sticky",
    top: "2rem",
  },
  avatar: {
    width: "6rem",
    height: "6rem",
    borderRadius: "9999px",
    background: "linear-gradient(to bottom right, #3b82f6, #9333ea)",
    margin: "0 auto 1rem",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "1.875rem",
    fontWeight: "bold",
    color: "#fff",
  },
  heading: {
    fontSize: "1.25rem",
    fontWeight: 600,
    marginBottom: "1rem",
    display: "flex",
    alignItems: "center",
  },
  tabNav: {
    display: "flex",
    gap: "2rem",
    borderBottom: "1px solid #e5e7eb",
    padding: "0 1.5rem",
  },
  tabButton: (active) => ({
    padding: "1rem 0.25rem",
    borderBottom: `2px solid ${active ? "#3b82f6" : "transparent"}`,
    color: active ? "#2563eb" : "#6b7280",
    fontWeight: 500,
    fontSize: "0.875rem",
    cursor: "pointer",
    transition: "all 0.2s",
  }),
  statBox: (color) => ({
    padding: "0.75rem",
    borderRadius: "0.5rem",
    textAlign: "center",
    backgroundColor: color,
  }),
  button: (bg, hover) => ({
    width: "100%",
    padding: "0.75rem",
    borderRadius: "0.5rem",
    fontWeight: 500,
    color: "#fff",
    backgroundColor: bg,
    transition: "background-color 0.2s",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: "0.75rem",
  }),
  smallButton: {
    flex: 1,
    padding: "0.5rem 1rem",
    borderRadius: "0.5rem",
    border: "1px solid #d1d5db",
    color: "#4b5563",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    cursor: "pointer",
  },
  expertiseTag: {
    padding: "0.25rem 0.75rem",
    backgroundColor: "#dbeafe",
    color: "#1e40af",
    borderRadius: "9999px",
    fontSize: "0.875rem",
    fontWeight: 500,
  },
  listItem: {
    display: "flex",
    alignItems: "flex-start",
    marginBottom: "0.75rem",
    color: "#374151",
  },
};

const MentorDetailPage = () => {
  const [isBookmarked, setIsBookmarked] = useState(false);
  const [activeTab, setActiveTab] = useState("about");

  // --- same mentor data as before (not repeating for brevity) ---
  const mentor = {
    name: "Sarah Chen",
    title: "Senior Product Manager",
    company: "Google",
    location: "San Francisco, CA",
    rating: 4.9,
    reviewCount: 127,
    experience: 8,
    mentees: 45,
    responseTime: "Within 2 hours",
    languages: ["English", "Mandarin", "Spanish"],
    timezone: "PST (UTC-8)",
    bio: "Passionate product manager with 8+ years of experience...",
    expertise: ["Product Strategy", "User Research", "Agile/Scrum"],
    achievements: ["Led product launch that generated $50M", "Speaker at ProductCon 2023"],
  };

  const tabs = [
    { id: "about", label: "About" },
    { id: "experience", label: "Experience" },
    { id: "availability", label: "Availability" },
    { id: "reviews", label: "Reviews" },
  ];

  const renderStars = (rating) => (
    <div style={{ display: "flex", gap: "0.25rem" }}>
      {[1, 2, 3, 4, 5].map((star) => (
        <Star
          key={star}
          size={16}
          style={{
            color: star <= rating ? "#facc15" : "#d1d5db",
            fill: star <= rating ? "#facc15" : "none",
          }}
        />
      ))}
    </div>
  );

  const renderAboutTab = () => (
    <div style={{ display: "grid", gap: "1.5rem" }}>
      <div style={styles.card}>
        <h3 style={styles.heading}>About</h3>
        <p style={{ color: "#374151", lineHeight: "1.6" }}>{mentor.bio}</p>
      </div>

      <div style={styles.card}>
        <h3 style={styles.heading}>
          <Target size={20} style={{ marginRight: "0.5rem" }} /> Expertise
        </h3>
        <div style={{ display: "flex", flexWrap: "wrap", gap: "0.5rem" }}>
          {mentor.expertise.map((skill, i) => (
            <span key={i} style={styles.expertiseTag}>
              {skill}
            </span>
          ))}
        </div>
      </div>

      <div style={styles.card}>
        <h3 style={styles.heading}>
          <Award size={20} style={{ marginRight: "0.5rem" }} /> Achievements
        </h3>
        <ul style={{ marginTop: "0.75rem" }}>
          {mentor.achievements.map((a, i) => (
            <li key={i} style={styles.listItem}>
              <CheckCircle size={16} style={{ color: "#22c55e", marginRight: "0.5rem" }} />
              {a}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );

  return (
    <div style={styles.page}>
      <div style={styles.container}>
        {/* Profile Sidebar */}
        <div style={styles.profileCard}>
          <div style={styles.card}>
            <div style={{ textAlign: "center" }}>
              <div style={styles.avatar}>SC</div>
              <h1 style={{ fontSize: "1.5rem", fontWeight: "bold" }}>{mentor.name}</h1>
              <p style={{ color: "#2563eb", fontWeight: 500 }}>{mentor.title}</p>
              <p style={{ color: "#6b7280", marginBottom: "1rem" }}>{mentor.company}</p>

              <div style={{ display: "flex", justifyContent: "center", gap: "0.25rem" }}>
                {renderStars(mentor.rating)}
                <span style={{ fontWeight: "bold" }}>{mentor.rating}</span>
                <span style={{ color: "#6b7280" }}>({mentor.reviewCount})</span>
              </div>
            </div>

            {/* Action Buttons */}
            <div style={{ marginTop: "1.5rem" }}>
              <button style={styles.button("#2563eb")}>
                <Calendar size={18} style={{ marginRight: "0.5rem" }} /> Book Session
              </button>
              <button style={styles.button("#16a34a")}>
                <MessageCircle size={18} style={{ marginRight: "0.5rem" }} /> Send Message
              </button>

              <div style={{ display: "flex", gap: "0.5rem" }}>
                <button
                  onClick={() => setIsBookmarked(!isBookmarked)}
                  style={{
                    ...styles.smallButton,
                    backgroundColor: isBookmarked ? "#fee2e2" : "#f9fafb",
                    borderColor: isBookmarked ? "#fecaca" : "#d1d5db",
                    color: isBookmarked ? "#dc2626" : "#4b5563",
                  }}
                >
                  <Heart size={18} />
                </button>
                <button style={styles.smallButton}>
                  <Share2 size={18} />
                </button>
                <button style={styles.smallButton}>
                  <Flag size={18} />
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div>
          {/* Tabs */}
          <div style={styles.card}>
            <nav style={styles.tabNav}>
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  style={styles.tabButton(activeTab === tab.id)}
                >
                  {tab.label}
                </button>
              ))}
            </nav>
          </div>

          {/* Tab Content */}
          <div style={{ marginTop: "1.5rem" }}>
            {activeTab === "about" && renderAboutTab()}
            {/* You can expand experience, availability, reviews in same inline style structure */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default MentorDetailPage;
