import React from "react";

const ChatWindow = ({ conversation }) => {
  const styles = {
    container: {
      flex: 1,
      display: "flex",
      flexDirection: "column",
      height: "100%",
      backgroundColor: "#000",
      color: "#fff",
    },
    header: {
      padding: "16px",
      borderBottom: "1px solid #333",
      display: "flex",
      alignItems: "center",
    },
    profilePhoto: {
      width: "40px",
      height: "40px",
      borderRadius: "50%",
      marginRight: "12px",
    },
    messages: {
      flex: 1,
      padding: "16px",
      overflowY: "auto",
    },
    inputArea: {
      padding: "12px",
      borderTop: "1px solid #333",
      display: "flex",
    },
    input: {
      flex: 1,
      padding: "10px",
      borderRadius: "8px",
      border: "none",
      outline: "none",
      backgroundColor: "#222",
      color: "#fff",
    },
    button: {
      marginLeft: "8px",
      padding: "10px 20px",
      border: "none",
      borderRadius: "8px",
      backgroundColor: "#4f46e5",
      color: "#fff",
      cursor: "pointer",
    },
  };

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <img src={conversation.profilePhoto} alt="profile" style={styles.profilePhoto} />
        <div>
          <div style={{ fontWeight: "bold" }}>{conversation.name}</div>
          <div style={{ fontSize: "12px", color: "#aaa" }}>Active now</div>
        </div>
      </div>

      {/* Messages */}
      <div style={styles.messages}>
        <p><b>{conversation.name}</b>: {conversation.lastMessage}</p>
        <p><b>You</b>: Sure, letâ€™s do it!</p>
      </div>

      {/* Input */}
      <div style={styles.inputArea}>
        <input style={styles.input} placeholder="Type a message..." />
        <button style={styles.button}>Send</button>
      </div>
    </div>
  );
};

export default ChatWindow;
