import React from "react";

const NotificationsTab = ({ notifications, setNotifications }) => {
  const handleChange = (field, value) => {
    setNotifications((prev) => ({ ...prev, [field]: value }));
  };

  const renderToggle = (label, desc, field) => (
    <div className="flex items-center justify-between py-3 border-b border-gray-100">
      <div>
        <p className="font-medium">{label}</p>
        <p className="text-sm text-gray-600">{desc}</p>
      </div>
      <label className="relative inline-flex items-center cursor-pointer">
        <input
          type="checkbox"
          checked={notifications[field]}
          onChange={(e) => handleChange(field, e.target.checked)}
          className="sr-only"
        />
        <div
          className={`w-11 h-6 rounded-full transition-colors ${
            notifications[field] ? "bg-blue-600" : "bg-gray-200"
          }`}
        >
          <div
            className={`w-4 h-4 bg-white rounded-full transition-transform transform ${
              notifications[field] ? "translate-x-6" : "translate-x-1"
            } mt-1`}
          ></div>
        </div>
      </label>
    </div>
  );

  return (
    <div className="space-y-6">
      <div className="bg-white rounded-lg p-6 shadow-sm border">
        <h3 className="text-lg font-semibold mb-4">Notification Preferences</h3>
        {renderToggle("Email Notifications", "Receive notifications via email", "emailNotifications")}
        {renderToggle("Push Notifications", "Receive push notifications in your browser", "pushNotifications")}
        {renderToggle("SMS Notifications", "Receive important notifications via SMS", "smsNotifications")}
        {renderToggle("Task Reminders", "Get reminders for upcoming tasks", "taskReminders")}
        {renderToggle("Project Updates", "Stay updated on project progress", "projectUpdates")}
        {renderToggle("Team Mentions", "Get notified when mentioned by team members", "teamMentions")}
        {renderToggle("Marketing Emails", "Receive product updates and tips", "marketingEmails")}
      </div>
    </div>
  );
};

export default NotificationsTab;
