import React, { useState } from "react";
import {
  User,
  Bell,
  Shield,
  Palette,
  Save,
  Camera,
  Mail,
  Phone,
  MapPin,
} from "lucide-react";

const containerStyle = {
  minHeight: "100vh",
  backgroundColor: "#f9fafb",
};

const headerStyle = {
  backgroundColor: "white",
  borderBottom: "1px solid #e5e7eb",
};

const contentWrapperStyle = {
  maxWidth: "1280px",
  margin: "0 auto",
  padding: "2rem 1rem",
  display: "flex",
  gap: "2rem",
};

const sidebarStyle = {
  width: "16rem",
  backgroundColor: "white",
  border: "1px solid #e5e7eb",
  borderRadius: "0.5rem",
};

const tabButtonStyle = (active) => ({
  width: "100%",
  display: "flex",
  alignItems: "center",
  gap: "0.75rem",
  padding: "0.5rem 0.75rem",
  borderRadius: "0.5rem",
  borderRight: active ? "2px solid #2563eb" : "none",
  backgroundColor: active ? "#eff6ff" : "transparent",
  color: active ? "#1d4ed8" : "#374151",
  fontWeight: 500,
  cursor: "pointer",
});

const cardStyle = {
  backgroundColor: "white",
  border: "1px solid #e5e7eb",
  borderRadius: "0.5rem",
  padding: "1.5rem",
};

const inputStyle = {
  width: "100%",
  padding: "0.5rem 0.75rem",
  border: "1px solid #d1d5db",
  borderRadius: "0.5rem",
  outline: "none",
};

const buttonPrimary = {
  display: "flex",
  alignItems: "center",
  gap: "0.5rem",
  padding: "0.75rem 1.5rem",
  backgroundColor: "#2563eb",
  color: "white",
  borderRadius: "0.5rem",
  cursor: "pointer",
  border: "none",
};

const SettingsPage = () => {
  const [activeTab, setActiveTab] = useState("profile");
  const [profile, setProfile] = useState({
    firstName: "John",
    lastName: "Doe",
    email: "john.doe@email.com",
    phone: "+1 (555) 123-4567",
    location: "New York, NY",
    bio: "Senior Software Engineer passionate about creating innovative solutions.",
    avatar: null,
  });

  const handleProfileChange = (field, value) => {
    setProfile((prev) => ({ ...prev, [field]: value }));
  };

  // -------- Profile Tab ----------
  const ProfileTab = () => (
    <div style={cardStyle}>
      <h3 style={{ fontSize: "1.125rem", fontWeight: "600", marginBottom: "1rem" }}>
        Profile Information
      </h3>

      <div style={{ display: "flex", alignItems: "center", gap: "1rem", marginBottom: "1.5rem" }}>
        <div
          style={{
            width: "5rem",
            height: "5rem",
            borderRadius: "50%",
            background: "linear-gradient(to bottom right, #3b82f6, #8b5cf6)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            color: "white",
            fontSize: "1.5rem",
            fontWeight: "bold",
          }}
        >
          {profile.firstName[0]}
          {profile.lastName[0]}
        </div>
        <button
          style={{
            display: "flex",
            alignItems: "center",
            gap: "0.5rem",
            padding: "0.5rem 1rem",
            border: "1px solid #d1d5db",
            borderRadius: "0.5rem",
            cursor: "pointer",
            backgroundColor: "white",
          }}
        >
          <Camera size={16} />
          <span>Change Photo</span>
        </button>
      </div>

      {/* Inputs */}
      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "1rem" }}>
        <div>
          <label style={{ fontSize: "0.875rem", fontWeight: "500", color: "#374151" }}>
            First Name
          </label>
          <input
            type="text"
            value={profile.firstName}
            onChange={(e) => handleProfileChange("firstName", e.target.value)}
            style={inputStyle}
          />
        </div>
        <div>
          <label style={{ fontSize: "0.875rem", fontWeight: "500", color: "#374151" }}>
            Last Name
          </label>
          <input
            type="text"
            value={profile.lastName}
            onChange={(e) => handleProfileChange("lastName", e.target.value)}
            style={inputStyle}
          />
        </div>
      </div>

      <div style={{ marginTop: "1rem" }}>
        <label style={{ fontSize: "0.875rem", fontWeight: "500", color: "#374151" }}>
          <Mail size={16} style={{ display: "inline", marginRight: "0.25rem" }} />
          Email
        </label>
        <input
          type="email"
          value={profile.email}
          onChange={(e) => handleProfileChange("email", e.target.value)}
          style={inputStyle}
        />
      </div>
    </div>
  );

  return (
    <div style={containerStyle}>
      {/* Header */}
      <div style={headerStyle}>
        <div style={{ maxWidth: "1280px", margin: "0 auto", padding: "1.5rem 1rem" }}>
          <h1 style={{ fontSize: "1.875rem", fontWeight: "700", color: "#111827" }}>Settings</h1>
          <p style={{ marginTop: "0.5rem", color: "#4b5563" }}>
            Manage your account settings and preferences
          </p>
        </div>
      </div>

      {/* Content */}
      <div style={contentWrapperStyle}>
        {/* Sidebar */}
        <div style={sidebarStyle}>
          <nav style={{ padding: "1rem" }}>
            <button
              style={tabButtonStyle(activeTab === "profile")}
              onClick={() => setActiveTab("profile")}
            >
              <User size={20} />
              <span>Profile</span>
            </button>
            <button
              style={tabButtonStyle(activeTab === "notifications")}
              onClick={() => setActiveTab("notifications")}
            >
              <Bell size={20} />
              <span>Notifications</span>
            </button>
            <button
              style={tabButtonStyle(activeTab === "preferences")}
              onClick={() => setActiveTab("preferences")}
            >
              <Palette size={20} />
              <span>Preferences</span>
            </button>
            <button
              style={tabButtonStyle(activeTab === "security")}
              onClick={() => setActiveTab("security")}
            >
              <Shield size={20} />
              <span>Security</span>
            </button>
          </nav>
        </div>

        {/* Main */}
        <div style={{ flex: 1 }}>
          {activeTab === "profile" && <ProfileTab />}

          {/* Save Button */}
          <div style={{ marginTop: "2rem", display: "flex", justifyContent: "flex-end" }}>
            <button style={buttonPrimary}>
              <Save size={20} />
              <span>Save Changes</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SettingsPage;
