import React, { useState } from "react";
import ConversationItem from "../components/InboxPage/ConversationItem";
import ChatWindow from "../components/InboxPage/ChatWindow";

const InboxPage = () => {
  const [conversations] = useState([
    {
      id: 1,
      name: "Alice Johnson",
      lastMessage: "Can we schedule the call tomorrow?",
      time: "10:30 AM",
      unread: 2,
      profilePhoto: "https://i.pravatar.cc/150?img=5",
    },
    {
      id: 2,
      name: "David Smith",
      lastMessage: "Thanks for the help! üôè",
      time: "Yesterday",
      unread: 0,
      profilePhoto: "https://i.pravatar.cc/150?img=11",
    },
    {
      id: 3,
      name: "Priya Sharma",
      lastMessage: "I shared the document with you.",
      time: "2 days ago",
      unread: 5,
      profilePhoto: "https://i.pravatar.cc/150?img=32",
    },
  ]);

  const [selectedChat, setSelectedChat] = useState(null);

  const styles = {
    container: {
      display: "flex",
      height: "100vh",
      backgroundColor: "#f9fafb",
      color: "#111",
      fontFamily: "Arial, sans-serif",
    },
    sidebar: {
      width: "320px",
      backgroundColor: "#fff",
      borderRight: "1px solid #e5e7eb",
      display: "flex",
      flexDirection: "column",
    },
    header: {
      padding: "16px",
      borderBottom: "1px solid #e5e7eb",
      fontWeight: "bold",
      fontSize: "18px",
      color: "#1f2937",
    },
    searchBox: {
      padding: "10px",
      margin: "10px",
      borderRadius: "8px",
      border: "1px solid #d1d5db",
      backgroundColor: "#f3f4f6",
      color: "#111",
    },
    conversations: {
      flex: 1,
      overflowY: "auto",
    },
    chatArea: {
      flex: 1,
      backgroundColor: "#fff",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      borderLeft: "1px solid #e5e7eb",
    },
    emptyState: {
      textAlign: "center",
      color: "#6b7280",
    },
    emptyIcon: {
      fontSize: "50px",
      marginBottom: "20px",
      color: "#9ca3af",
    },
    button: {
      marginTop: "12px",
      padding: "10px 20px",
      border: "none",
      borderRadius: "8px",
      backgroundColor: "#3b82f6",
      color: "#fff",
      cursor: "pointer",
    },
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <div style={styles.header}>manish_rawat_1331</div>
        <input style={styles.searchBox} type="text" placeholder="Search..." />
        <div style={styles.conversations}>
          {conversations.map((conv) => (
            <ConversationItem
              key={conv.id}
              conversation={conv}
              onClick={() => setSelectedChat(conv)}
              isActive={selectedChat?.id === conv.id}
            />
          ))}
        </div>
      </div>

      {/* Chat Area */}
      <div style={styles.chatArea}>
        {selectedChat ? (
          <ChatWindow conversation={selectedChat} />
        ) : (
          <div style={styles.emptyState}>
            <div style={styles.emptyIcon}>üí¨</div>
            <h2>Your messages</h2>
            <p>Send a message to start a chat.</p>
            <button style={styles.button}>Send message</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default InboxPage;
