import React, { useState } from "react";
import { Plus, X, BookOpen, GraduationCap, User, Sparkles } from "lucide-react";

const MenteeForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    college: "",
    school: "",
    qualification: "",
    softSkills: []
  });

  const [newSkill, setNewSkill] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const addSkill = () => {
    if (
      newSkill.trim() &&
      !formData.softSkills.includes(newSkill.trim())
    ) {
      setFormData((prev) => ({
        ...prev,
        softSkills: [...prev.softSkills, newSkill.trim()]
      }));
      setNewSkill("");
    }
  };

  const removeSkill = (skillToRemove) => {
    setFormData((prev) => ({
      ...prev,
      softSkills: prev.softSkills.filter((skill) => skill !== skillToRemove)
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (formData.softSkills.length === 0) {
      alert("Please add at least one soft skill");
      return;
    }

    onSubmit({
      ...formData,
      age: parseInt(formData.age)
    });
  };

  const isFormValid =
    formData.name &&
    formData.age &&
    formData.college &&
    formData.school &&
    formData.qualification &&
    formData.softSkills.length > 0;

  return (
    <div className="card">
      <div className="text-center mb-8">
        <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
          <BookOpen className="w-8 h-8 text-green-600" />
        </div>
        <h2 className="text-3xl font-bold text-gray-900">Mentee Registration</h2>
        <p className="text-gray-600 mt-2">
          Fill out your details to start your learning journey
        </p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-8">
        {/* Section 1 - Personal Details */}
        <div className="p-5 border rounded-xl shadow-sm bg-gray-50">
          <h3 className="flex items-center gap-2 text-xl font-semibold text-gray-800 mb-4">
            <User className="w-5 h-5 text-green-600" /> Personal Details
          </h3>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Full Name *
              </label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                className="input-field"
                placeholder="Enter your full name"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Age *
              </label>
              <input
                type="number"
                name="age"
                value={formData.age}
                onChange={handleInputChange}
                className="input-field"
                placeholder="Enter your age"
                min="16"
                max="100"
              />
            </div>
          </div>
        </div>

        {/* Section 2 - Education Details */}
        <div className="p-5 border rounded-xl shadow-sm bg-gray-50">
          <h3 className="flex items-center gap-2 text-xl font-semibold text-gray-800 mb-4">
            <GraduationCap className="w-5 h-5 text-green-600" /> Education
            Details
          </h3>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                College/University *
              </label>
              <input
                type="text"
                name="college"
                value={formData.college}
                onChange={handleInputChange}
                className="input-field"
                placeholder="Enter your college/university"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                School *
              </label>
              <input
                type="text"
                name="school"
                value={formData.school}
                onChange={handleInputChange}
                className="input-field"
                placeholder="Enter your school"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Highest Qualification *
              </label>
              <input
                type="text"
                name="qualification"
                value={formData.qualification}
                onChange={handleInputChange}
                className="input-field"
                placeholder="e.g., Bachelor of Science"
              />
            </div>
          </div>
        </div>

        {/* Section 3 - Soft Skills */}
        <div className="p-5 border rounded-xl shadow-sm bg-gray-50">
          <h3 className="flex items-center gap-2 text-xl font-semibold text-gray-800 mb-4">
            <Sparkles className="w-5 h-5 text-green-600" /> Soft Skills
          </h3>
          <div className="space-y-3">
            <div className="flex gap-2">
              <input
                type="text"
                value={newSkill}
                onChange={(e) => setNewSkill(e.target.value)}
                className="input-field flex-1"
                placeholder="e.g., Communication, Leadership"
                onKeyPress={(e) => e.key === "Enter" && (e.preventDefault(), addSkill())}
              />
              <button
                type="button"
                onClick={addSkill}
                className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg transition-colors"
              >
                <Plus className="w-4 h-4" />
              </button>
            </div>

            {formData.softSkills.length > 0 && (
              <div className="flex flex-wrap gap-2">
                {formData.softSkills.map((skill, index) => (
                  <span
                    key={index}
                    className="inline-flex items-center gap-2 bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm"
                  >
                    {skill}
                    <button
                      type="button"
                      onClick={() => removeSkill(skill)}
                      className="text-green-600 hover:text-green-800"
                    >
                      <X className="w-3 h-3" />
                    </button>
                  </span>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          disabled={!isFormValid}
          className={`w-full py-3 px-4 rounded-lg font-medium transition-colors duration-200 ${
            isFormValid
              ? "bg-green-600 hover:bg-green-700 text-white"
              : "bg-gray-300 text-gray-500 cursor-not-allowed"
          }`}
        >
          Submit Registration
        </button>
      </form>
    </div>
  );
};

export default MenteeForm;
