import React, { useState } from "react";
import { Search } from "lucide-react";
import MentorGrid from "../components/MentorGrid";

const mentorsData = [ /* ... same as before ... */ ];

export default function ExplorePage() {
  const [search, setSearch] = useState("");
  const filteredMentors = mentorsData.filter((mentor) =>
    mentor.tech.toLowerCase().includes(search.toLowerCase())
  );
  const categories = [...new Set(mentorsData.map((m) => m.field))];

  // ✅ Inline CSS styles
  const styles = {
    page: {
      display: "flex",
      justifyContent: "center",
      alignItems: "flex-start",
      backgroundColor: "#f9fafb",
      minHeight: "100vh",
      width: "100%",
    },
    container: {
      width: "100%",
      maxWidth: "800px",
      padding: "2rem",
    },
    title: {
      fontSize: "1.875rem",
      fontWeight: "bold",
      marginBottom: "1.5rem",
      color: "#1f2937",
      textAlign: "center",
    },
    searchBox: {
      display: "flex",
      alignItems: "center",
      backgroundColor: "white",
      border: "1px solid #e5e7eb",
      borderRadius: "0.5rem",
      boxShadow: "0 1px 2px rgba(0,0,0,0.05)",
      padding: "0.75rem",
      marginBottom: "2.5rem",
      width: "100%",
      maxWidth: "400px",
      marginLeft: "auto",
      marginRight: "auto",
    },
    input: {
      width: "100%",
      outline: "none",
      border: "none",
      color: "#374151",
      fontSize: "1rem",
    },
    sectionTitle: {
      fontSize: "1.25rem",
      fontWeight: "600",
      marginBottom: "1rem",
      color: "#374151",
      textAlign: "center",
    },
    fieldTitle: {
      fontSize: "1.125rem",
      fontWeight: "bold",
      color: "#2563eb",
      marginBottom: "1rem",
      textAlign: "center",
    },
    noResult: {
      color: "#6b7280",
      textAlign: "center",
    },
  };

  return (
    <div style={styles.page}>
      <div style={styles.container}>
        <h1 style={styles.title}>Explore Mentors</h1>

        {/* Search Bar */}
        <div style={styles.searchBox}>
          <Search color="#6b7280" size={20} style={{ marginRight: "0.5rem" }} />
          <input
            type="text"
            placeholder="Search mentors by technology..."
            style={styles.input}
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>

        {/* Search Results */}
        {search && (
          <div style={{ marginBottom: "3rem" }}>
            <h2 style={styles.sectionTitle}>Results</h2>
            {filteredMentors.length > 0 ? (
              <MentorGrid mentors={filteredMentors} small />
            ) : (
              <p style={styles.noResult}>No mentors found for "{search}"</p>
            )}
          </div>
        )}

        {/* Top Mentors by Field */}
        <div>
          <h2 style={styles.sectionTitle}>Top Mentors by Field</h2>
          {categories.map((field, idx) => {
            const topMentors = mentorsData
              .filter((m) => m.field === field)
              .slice(0, 2);
            return (
              <div key={idx} style={{ marginBottom: "2.5rem" }}>
                <h3 style={styles.fieldTitle}>{field}</h3>
                <MentorGrid mentors={topMentors} small />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
