import React, { useState } from "react";
import { Plus, X, GraduationCap } from "lucide-react";

const MentorForm = ({ onSubmit }) => {
  const [step, setStep] = useState(1);
  const totalSteps = 4;

  const [formData, setFormData] = useState({
    name: "",
    age: "",
    gender: "",
    education: "",
    college: "",
    stream: "",
    yearsOfExperience: "",
    areasOfProficiency: [],
  });

  const [newSkill, setNewSkill] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const addSkill = () => {
    if (
      newSkill.trim() &&
      !formData.areasOfProficiency.includes(newSkill.trim())
    ) {
      setFormData((prev) => ({
        ...prev,
        areasOfProficiency: [...prev.areasOfProficiency, newSkill.trim()],
      }));
      setNewSkill("");
    }
  };

  const removeSkill = (skillToRemove) => {
    setFormData((prev) => ({
      ...prev,
      areasOfProficiency: prev.areasOfProficiency.filter(
        (skill) => skill !== skillToRemove
      ),
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      ...formData,
      age: parseInt(formData.age),
      yearsOfExperience: parseInt(formData.yearsOfExperience),
    });
  };

  const nextStep = () => setStep((prev) => Math.min(prev + 1, totalSteps));
  const prevStep = () => setStep((prev) => Math.max(prev - 1, 1));

  // progress percentage
  const progress = (step / totalSteps) * 100;

  return (
    <div className="card max-w-2xl mx-auto">
      {/* Progress Bar */}
      <div className="w-full bg-gray-200 rounded-full h-2 mb-6">
        <div
          className="bg-primary-600 h-2 rounded-full transition-all duration-300"
          style={{ width: `${progress}%` }}
        />
      </div>

      <div className="text-center mb-6">
        <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
          <GraduationCap className="w-8 h-8 text-primary-600" />
        </div>
        <h2 className="text-2xl font-bold text-gray-900">
          Mentor Registration
        </h2>
        <p className="text-gray-600 mt-2">
          Step {step} of {totalSteps}
        </p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* STEP 1: Personal Info */}
        {step === 1 && (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Full Name *
              </label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                className="input-field"
                placeholder="Enter your full name"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Age *
              </label>
              <input
                type="number"
                name="age"
                value={formData.age}
                onChange={handleInputChange}
                className="input-field"
                placeholder="Enter your age"
                min="18"
                max="100"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Gender *
              </label>
              <select
                name="gender"
                value={formData.gender}
                onChange={handleInputChange}
                className="input-field"
                required
              >
                <option value="">Select gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
              </select>
            </div>
          </div>
        )}

        {/* STEP 2: Education */}
        {step === 2 && (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Highest Education Level *
              </label>
              <select
                name="education"
                value={formData.education}
                onChange={handleInputChange}
                className="input-field"
                required
              >
                <option value="">Select education level</option>
                <option value="graduation">Graduation</option>
                <option value="post-graduation">Post-Graduation</option>
                <option value="phd">PhD</option>
                <option value="other">Other</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                College/University *
              </label>
              <input
                type="text"
                name="college"
                value={formData.college}
                onChange={handleInputChange}
                className="input-field"
                placeholder="Enter your college/university"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Field of Study *
              </label>
              <input
                type="text"
                name="stream"
                value={formData.stream}
                onChange={handleInputChange}
                className="input-field"
                placeholder="e.g., Computer Science, Engineering"
                required
              />
            </div>
          </div>
        )}

        {/* STEP 3: Experience */}
        {step === 3 && (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Years of Industry Experience *
              </label>
              <input
                type="number"
                name="yearsOfExperience"
                value={formData.yearsOfExperience}
                onChange={handleInputChange}
                className="input-field"
                placeholder="Enter years of experience"
                min="0"
                max="50"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Areas of Proficiency *
              </label>
              <div className="flex gap-2 mb-3">
                <input
                  type="text"
                  value={newSkill}
                  onChange={(e) => setNewSkill(e.target.value)}
                  className="input-field flex-1"
                  placeholder="e.g., Python, Machine Learning"
                  onKeyPress={(e) =>
                    e.key === "Enter" && (e.preventDefault(), addSkill())
                  }
                />
                <button
                  type="button"
                  onClick={addSkill}
                  className="btn-primary px-4 py-2"
                >
                  <Plus className="w-4 h-4" />
                </button>
              </div>
              {formData.areasOfProficiency.length > 0 && (
                <div className="flex flex-wrap gap-2">
                  {formData.areasOfProficiency.map((skill, index) => (
                    <span
                      key={index}
                      className="inline-flex items-center gap-2 bg-primary-100 text-primary-800 px-3 py-1 rounded-full text-sm"
                    >
                      {skill}
                      <button
                        type="button"
                        onClick={() => removeSkill(skill)}
                        className="text-primary-600 hover:text-primary-800"
                      >
                        <X className="w-3 h-3" />
                      </button>
                    </span>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {/* STEP 4: Review & Submit */}
        {step === 4 && (
          <div className="space-y-4">
            <h3 className="text-lg font-semibold mb-2">Review Your Details</h3>
            <ul className="list-disc pl-5 text-gray-700 space-y-1">
              <li>Name: {formData.name}</li>
              <li>Age: {formData.age}</li>
              <li>Gender: {formData.gender}</li>
              <li>Education: {formData.education}</li>
              <li>College: {formData.college}</li>
              <li>Stream: {formData.stream}</li>
              <li>Experience: {formData.yearsOfExperience} years</li>
              <li>
                Skills: {formData.areasOfProficiency.join(", ") || "None"}
              </li>
            </ul>
          </div>
        )}

        {/* Navigation Buttons */}
        <div className="flex justify-between pt-4">
          {step > 1 ? (
            <button
              type="button"
              onClick={prevStep}
              className="px-4 py-2 rounded-lg bg-gray-200 text-gray-700 hover:bg-gray-300"
            >
              Back
            </button>
          ) : (
            <div />
          )}

          {step < totalSteps ? (
            <button
              type="button"
              onClick={nextStep}
              className="px-4 py-2 rounded-lg bg-primary-600 text-white hover:bg-primary-700"
            >
              Next
            </button>
          ) : (
            <button
              type="submit"
              className="px-6 py-2 rounded-lg bg-green-600 text-white hover:bg-green-700"
            >
              Submit
            </button>
          )}
        </div>
      </form>
    </div>
  );
};

export default MentorForm;
