import React from "react";

const ConversationItem = ({ conversation, onClick, isActive }) => {
  const styles = {
    container: {
      display: "flex",
      alignItems: "center",
      padding: "12px",
      cursor: "pointer",
      backgroundColor: isActive ? "#222" : "transparent",
      borderBottom: "1px solid #333",
    },
    profilePhoto: {
      width: "45px",
      height: "45px",
      borderRadius: "50%",
      marginRight: "12px",
    },
    details: {
      flex: 1,
      overflow: "hidden",
    },
    name: {
      fontWeight: "bold",
      fontSize: "14px",
      whiteSpace: "nowrap",
      overflow: "hidden",
      textOverflow: "ellipsis",
    },
    lastMessage: {
      fontSize: "12px",
      color: "#aaa",
      whiteSpace: "nowrap",
      overflow: "hidden",
      textOverflow: "ellipsis",
    },
    time: {
      fontSize: "11px",
      color: "#888",
      marginLeft: "8px",
    },
    unread: {
      backgroundColor: "#2563eb",
      color: "#fff",
      borderRadius: "50%",
      fontSize: "12px",
      padding: "2px 6px",
      marginLeft: "8px",
    },
  };

  return (
    <div style={styles.container} onClick={onClick}>
      <img src={conversation.profilePhoto} alt="profile" style={styles.profilePhoto} />
      <div style={styles.details}>
        <div style={styles.name}>{conversation.name}</div>
        <div style={styles.lastMessage}>{conversation.lastMessage}</div>
      </div>
      <div>
        <span style={styles.time}>{conversation.time}</span>
        {conversation.unread > 0 && (
          <span style={styles.unread}>{conversation.unread}</span>
        )}
      </div>
    </div>
  );
};

export default ConversationItem;
